def text_in_message(text):
    if text.lower() in PREFIX__weather:
        return 0
    elif text.lower() in PREFIX__raining:
        return 1
    elif text.lower() in PREFIX__rower:
        return 2
    return 3


@client.event
async def on_message(message):
    if message.author != client.user:
        msg = message.content
        msg = removeAccents(msg)
        nb_q = text_in_message(msg)
        if nb_q == 0:
            try:
                await message.channel.send(embed=weather_message())
            except KeyError:
                await message.channel.send(embed=error_message())
        elif nb_q == 1:
            pass
        elif nb_q == 2:
            pass
        else:
            return


client.run(DISCORD_TOKEN)
